mapper.select:
SELECT * FROM _MAP M
M.NAME LIKE CONCAT('%', #{st}, '%') M.REGISTOR_ID LIKE CONCAT('%', #{st}, '%') LOWER(M.PROJECTION) LIKE CONCAT('%', #{st}, '%') 1=1
AND M.IMAGE_TYPE = #{iType}
AND M.UNITS = #{units}
AND M.REGIST_TIME BETWEEN TO_TIMESTAMP(#{sDate}, 'YYYY-MM-DD') AND TO_TIMESTAMP(#{eDate}, 'YYYY-MM-DD')
ORDER BY
M.ID ASC M.NAME ASC M.REGIST_TIME DESC M.ID DESC
LIMIT ${sz} OFFSET ${start}
LIMIT 0

SELECT COUNT(*) FROM _MAP M
M.NAME LIKE CONCAT('%', #{st}, '%') M.REGISTOR_ID LIKE CONCAT('%', #{st}, '%') LOWER(M.PROJECTION) LIKE CONCAT('%', #{st}, '%') 1=1
AND M.IMAGE_TYPE = #{iType}
AND M.UNITS = #{units}
AND M.REGIST_TIME BETWEEN TO_TIMESTAMP(#{sDate}, 'YYYY-MM-DD') AND TO_TIMESTAMP(#{eDate}, 'YYYY-MM-DD')

mapper.update:
UPDATE _MAP SET
NAME = #{name}, MAP_FILE_PATH = #{mapFilePath}, MIN_X = #{minX}, MAX_X = #{maxX}, MIN_Y = #{minY}, MAX_Y = #{maxY}, UNITS = #{units}, PROJECTION = #{projection}, IMAGE_TYPE = #{imageType}, IS_DEFAULT = #{isDefault}, REGISTOR_ID = #{registorId}, REGISTOR_NAME = #{registorName}, REGIST_TIME = #{registTime}
WHERE id = #{id}
